INSERT INTO `poem` (`poem_id`, `poet_id`, `poem_title`, `poem_highlight`, `poem_context`, `id`) VALUES
('1', '1', '"Chicago"', '<p>&nbsp;&nbsp;&nbsp; &nbsp;HOG Butcher for \r\n		the World,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Tool Maker, Stacker of Wheat,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Player with Railroads and the Nation&#39;s Freight Handler;...<p>', '<p>&nbsp;&nbsp;&nbsp; &nbsp;HOG Butcher for \r\n		the World,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Tool Maker, Stacker of Wheat,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Player with Railroads and the Nation&#39;s Freight Handler;<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Stormy, husky, brawling,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; City of the Big Shoulders:</p>\r\n        <p>They tell me you are wicked and I \r\n		believe them, for I<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; have seen your painted women under the gas lamps<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; luring the farm boys.<br>\r\n        And they tell me you are crooked and I answer: Yes, it<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; is true I have seen the gunman kill and go free to<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; kill again.<br>\r\n        And they tell me you are brutal and my reply is: On the<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; faces of women and children I have seen the marks<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; of wanton hunger.<br>\r\n        And having answered so I turn once more to those who<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; sneer at this my city, and I give them back the sneer<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; and say to them:<br>\r\n        Come and show me another city with lifted head singing<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; so proud to be alive and coarse and strong and cunning.<br>\r\n        Flinging magnetic curses amid the toil of piling job on<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; job, here is a tall bold slugger set vivid against the<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; little soft cities;</p>\r\n        <p>Fierce as a dog with tongue lapping \r\n		for action, cunning<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; as a savage pitted against the wilderness,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bareheaded,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Shoveling,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Wrecking,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Planning,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Building, breaking, \r\n		rebuilding,<br>\r\n        Under the smoke, dust all over his mouth, laughing with<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; white teeth,<br>\r\n        Under the terrible burden of destiny laughing as a young<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; man laughs,<br>\r\n        Laughing even as an ignorant fighter laughs who has<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; never lost a battle,<br>\r\n        Bragging and laughing that under his wrist is the pulse.<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; and under his ribs the heart of the people,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n		Laughing!<br>\r\n        Laughing the stormy, husky, brawling laughter of<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Youth, half-naked, sweating, proud to be Hog<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Butcher, Tool Maker, Stacker of Wheat, Player with<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp; Railroads and Freight Handler to the Nation.</p>', 1),
('2', '2', '"Chicago Congo"', '<p class=''epigraph''><i>(Sonata for an Orchestra)</i></p>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Chicago is an overgrown woman&nbsp;&nbsp;&nbsp;</div>\r\n\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wearing her skyscrapers </div>\r\n\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;like a necklace ... </div></p>', '<div class=''poem''>\r\n					<p class=''epigraph''><i>(Sonata for an Orchestra)</i></p>\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Chicago is an overgrown woman&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;wearing her skyscrapers </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;like a necklace ... </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Chicago&rsquo;s blood is kaleidoscopic&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Chicago&rsquo;s heart has a hundred auricles </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>From the Congo&nbsp;&nbsp;&nbsp;</div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to Chicago </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; is a long trek </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &mdash;as the crow flies</em> </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Sing to me of a red warrior moon victorious in a Congo sky ... show me a round dollar moon in the ragged blue purse of Chicago&rsquo;s heavens ... tell me of a hundred spoil laden blacks tramping home from the raid ... point me out a hundred brown men riding the elevated home on payday ... pick me the winners ... in Chicago? ... in the Congo? </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Skyscraper pinnacles rip great holes in the rubber balloon bag of the sky ... do spears kill quicker than printed words? ... midnight lies and cobra fangs ... ask me if civilization produces new forms of biting and tearing and killing ... see three million whites and two hundred thousand blacks civilized in Chicago </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>From the Congo&nbsp;&nbsp;&nbsp;</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to Chicago </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; is a long trek </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &mdash;as the crow flies</em> </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>I&rsquo;m a grownup man today Chicago </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>My bones are thick and stout </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(when I moved to new districts bombings&nbsp;&nbsp;&nbsp;</div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;couldn&rsquo;t break them) </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>My flesh is smooth and firm </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(look&mdash;the wounds you give me heal quickly)&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>See how the muscles ripple under my night-black skin&nbsp;&nbsp;&nbsp;</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>My strength comes not from resting </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>You should be proud of me Chicago </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>I&rsquo;ve got a lion&rsquo;s heart and a six-shooter </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>I&rsquo;ve got a fighter&rsquo;s fist and five newspapers </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>I&rsquo;ve got an eye for beauty and another for cash&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Nothing you&rsquo;ve got I can&rsquo;t have </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>A song dashes its rhythms in, my face like April rain </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>My song is a song of steel and bamboo, of brick flats and reed huts, of steamboats and slim canoes, of murder trials and jackal packs, of con men and pythons </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>My tune I get from automobiles and lions roaring, from the rustle of banknotes in a teller&rsquo;s window and the rustle of leaves in Transvaal trees </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>I ask you to find a better song, a louder song, a sweeter song&mdash; </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Here&rsquo;s something Wagner couldn&rsquo;t do </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>State Street is a wide gray band across Chicago&rsquo;s forehead&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>At night a white faced mother moon clothes Skyscrapers in gray silk </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>At night when clocks yawn and hours get lazy </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>At night when the jungle&rsquo;s a symphony in grays ... </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Oh mother moon, mother of earth, bringer of silver gifts&nbsp;&nbsp;&nbsp;</div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Bring a veil of stardust to wrap this Congo in </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Bring a shawl of moonmist to clothe Chicago&rsquo;s body </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Between the covers of books lie the bones of yesterdays&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Today is a new dollar </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>And </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>My city is money mad </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Across the street from the Ebenezer Baptist Church,&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;women with cast-iron faces peddle love </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>In the flat above William&rsquo;s Funeral Home </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;six couples sway to the St. Louis Blues </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Two doors away from the South Side Bank </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;three penny-brown men scorch their guts with four bit whiskey </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Dr. Jackson buys a Lincoln </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>His neighbor buys second hand, shoes </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&mdash;the artist who paints this town must </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;use a checkered canvas ... </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Tired looking houses of brown stone </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Ramshackle flats with sightless eyes </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>A surface car throws a handful of white sparks at cracked red bricks </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>An L train roars oaths at backyard clotheslines </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Mornings on South Parkway flats sit like silent cats watching the little green mice of buses running up and down the boulevard </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>And only grass has heard the secrets of vacant lots </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; * </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>This song has no tune. You cannot hum it. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>This song has no words. You cannot sing it. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>This song everybody knows, nobody knows. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>It is in a pattern of brown faces at the Wabash Y.M.C.A., a 35th Street gambling place, a Parkway theatre&mdash;you get it or you don&rsquo;t </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>It is a melody of everything and nothing </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>I saw twelve stars sitting along the edge of a four story flat&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>I saw a moon held by leafless tree fingers </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>I heard a shot tear huge holes in the blanket of silence&nbsp;&nbsp;&nbsp;</div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Later&mdash;just a little later&mdash;the moon got away and the stars stepped back into the sky </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>There will always be new wordless songs, new humless tunes&nbsp;&nbsp;&nbsp;</div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Chicago sings these songs each day&nbsp;&nbsp;&nbsp;</div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Chicago who wears her skyscrapers like a necklace ...</div>\r\n				</div>\r\n			\r\n		\r\n		\r\n			<div class=''credit''>\r\n					\r\n					\r\n					<p>\r\n					Frank Marshall Davis, &ldquo;Chicago&rsquo;s Congo&rdquo; from <em>Black Moods: Collected Poems.</em> Copyright &copy; 2002 by the Trustees of the University of Illinois.  Reprinted with the permission of the University of Illinois Press.<br /><br />\r\n					\r\n						\r\n						Source: <em>Black Man''s Verse</em> (University of Illinois Press, 1935)</em>\r\n					\r\n				</p>\r\n			</div>', 2),
('3', '3', '"Visiting a dead man on a summer day"', '<div style=''text-indent: -1em; padding-left: 1em;''>In flat America, in Chicago, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Graceland cemetery on the German North Side... </div>', '<div style=''text-indent: -1em; padding-left: 1em;''>In flat America, in Chicago, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Graceland cemetery on the German North Side. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Forty feet of Corinthian candle </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>celebrate Pullman embedded </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>lonely raisin in a cake of concrete. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>The Potter Palmers float </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in an island parthenon. </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Barons of hogfat, railroads and wheat </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>are postmarked with angels and lambs. </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>But the Getty tomb: white, snow patterned </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in a triangle of trees swims dappled with leaf shadow, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>sketched light arch within arch </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>delicate as fingernail moons. </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>The green doors should not be locked. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Doors of fern and flower should not be shut. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Louis Sullivan, I sit on your grave. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''><em>It is not now good weather for prophets.</em> </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Sun eddies on the steelsmoke air like sinking honey. </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>On the inner green door of the Getty tomb </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>(a thighbone''s throw from your stone) </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>a marvel of growing, blooming, thrusting into seed: </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>how all living wreathe and insinuate </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>in the circlet of repetition that never repeats: </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''><em>ever new birth never rebirth.</em> </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Each tide pool microcosm spiraling from your hand. </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Sullivan, you had another five years </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>when your society would give you work. </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Thirty years with want crackling in your hands. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Thirty after years with cities </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>flowering and turning grey in your beard. </div>\r\n<br/>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>All poets are unemployed nowadays. </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>My country marches in its sleep. </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>The past structures a heavy mausoleum </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>hiding its iron frame in masonry. </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Men burn like grass </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>while armies grow. </div>\r\n<br/>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Thirty years in the vast rumbling gut </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>of this society you stormed </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>to be used, screamed </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>no louder than any other breaking voice. </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>The waste of a good man </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>bleeds the future that''s come </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in Chicago, in flat America, </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>where the poor still bleed from the teeth, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>housed in sewers and filing cabinets, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>where prophets may spit into the wind </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>till anger sleets their eyes shut, </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>where this house that dances the seasons </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and the braid of all living </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and the joy of a man making his new good thing </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>is strange, irrelevant as a meteor, </div>\r\n\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>in Chicago, in flat America </div>\r\n					\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in this year of our burning. </div>\r\n<br/>\r\n				</div>\r\n	\r\n	<div class="credit">\r\n					\r\n					\r\n				<p> from <em>Circles on the Water:  Selected Poems of Marge Piercy</em> (New York:  Alfred A. Knopf, 1982).  First appeared in <em>Carleton Miscellany</em> (Winter 1967).  Copyright &copy;1982 by Marge Piercy and Middlemarsh, Inc.  Used by permission of the Wallace Literary Agency, Inc.<br /><br />\r\n					\r\n						\r\n				</p>\r\n			', 3),
('4', '4', '"At the Base of the Blues Tree: Una Ofrenda for Jimmy Davis, Chicago Blues Man"', '<p class="epigraph">(after the painting <em><em>&Aacute;</em>﻿rbol de Blues</em> by Roberto Valadez)</p>\r\n					<div style=''text-indent: -1em; padding-left: 1em;''><div class=''poempara''>The hands of this telling tree pull...</div>', '<div class="poem">\r\n					<p class="epigraph">(after the painting <em><em>&Aacute;</em>﻿rbol de Blues</em> by Roberto Valadez)</p>\r\n					<div style=''text-indent: -1em; padding-left: 1em;''><div class=''poempara''>The hands of this telling tree pull</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; him from the cobalt bowl sky,</div>\r\n<div class=''poempara''>bring Jimmy back to the ruins</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of Maxwell Street hunched in dusk.</div>\r\n<div class=''poempara''>He stood where strings intersected</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; frets like tracks meeting ties</div>\r\n<div class=''poempara''>on the Illinois Central&rsquo;s passage</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; from the Mississippi Delta,</div>\r\n<div class=''poempara''>then followed the bread crumbs</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of troubadours to this steel city.</div>\r\n<div class=''poempara''>Twelve-hour shifts beckoned</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mexicanos north on the Union Pacific</div>\r\n<div class=''poempara''>from Piedras Negras</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to Pilsen, Back of the Yards.</div>\r\n<div class=''poempara''>&nbsp;</div>\r\n<div class=''poempara''>How many grandparents, mothers and fathers,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pounded stake into soil,</div>\r\n<div class=''poempara''>snuck or were shoved into boxcars,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; leapt onto the train&rsquo;s iron rungs</div>\r\n<div class=''poempara''>as it shuddered along the rails</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to foundries where they poured</div>\r\n<div class=''poempara''>molten metal, faced hot licks</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of the blast furnace&rsquo;s growl and hiss?</div>\r\n<div class=''poempara''>How many lugged themselves north</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to slaughterhouses that drooled</div>\r\n<div class=''poempara''>rank puddles at their feet? This,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; so sons and daughters might step off</div>\r\n<div class=''poempara''>into another era, choose instruments of wood and metal,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; axes of their own timbre.</div>\r\n<div class=''poempara''>&nbsp;</div>\r\n<div class=''poempara''>This so Jimmy might mix his own palette&mdash;</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tune each turquoise pitch and purple tone</div>\r\n<div class=''poempara''>into an electric blues old as corridos,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; but new to the mexicanos who came each</div>\r\n<div class=''poempara''>Sunday to the market at Maxwell.</div>\r\n<div class=''poempara''>&nbsp;</div>\r\n<div class=''poempara''>New to the ears of Roberto, a boy, who would keep</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; his ranchera rhythms close</div>\r\n<div class=''poempara''>as a pair of old jeans but grow</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; into the blues as he listened</div>\r\n<div class=''poempara''>to the heirs of Papa Jackson&mdash;</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Johnny Young, Howlin&rsquo; Wolf, Nighthawk</div>\r\n<div class=''poempara''>and Maxwell Jimmy. Now Roberto sits in overalls</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; at an easel, splits</div>\r\n<div class=''poempara''>the hairs of his brush over how to compose</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this ofrenda, this homage. How</div>\r\n<div class=''poempara''>to take Jimmy back in time to the market</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; whose merchants once hustled</div>\r\n<div class=''poempara''>with tricks for trade ancient as Africa, as Toltec Mexico,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Maxwell, whose live blues bands</div>\r\n<div class=''poempara''>and car radios belting norte&ntilde;o mixes made</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; every Sunday a session.</div>\r\n<div class=''poempara''>&nbsp;</div>\r\n<div class=''poempara''>How to take Jimmy back</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; to Maxwell&rsquo;s gathering spot where he guzzles</div>\r\n<div class=''poempara''>from an aluminum garden blooming atop his amp,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; swings his ax&mdash;</div>\r\n<div class=''poempara''>fingers climbing its trunk, plucking twigs&mdash;</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while our feet tap the roots</div>\r\n<div class=''poempara''>of this wailing tree. Notes skip through alleys</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of his voice like gravel</div>\r\n<div class=''poempara''>pinging off windshields, lift a pitch</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; high enough to singe the autumn sky&mdash;</div>\r\n<div class=''poempara''>cousin to the red and green gritos of the barrio</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; only blocks away. Twelve bars spin</div>\r\n<div class=''poempara''>like metal rims, stack like hubcaps,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; chords crackling in the tin barrels of his laughter.</div>\r\n<div class=''poempara''>When the people gather, he sputters,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>What did the one-eyed woman say?</em></div>\r\n<div class=''poempara''>and waits for them to peer into the devil&rsquo;s draw</div>\r\n<div class=''poempara''>of his eyes before he roars,</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Where is my one-eyed man?</em></div>\r\n<div class=''poempara''>&nbsp;</div>\r\n<div class=''poempara''>But we barely get to the punch line, to the end</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of a set, before the mayor swings his ax,</div>\r\n<div class=''poempara''>a claw that rips this &aacute;rbol de blues up by its roots;</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; before Chicago swings its wrecking ball</div>\r\n<div class=''poempara''>to level homes, stores, restaurants, bars;</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; before troops graffiti-blast, scrub and cinch-sack it</div>\r\n<div class=''poempara''>all, but one empty wall</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; where someone has scrawled Jimmy&rsquo;s name</div>\r\n<div class=''poempara''>among the others in this hall of fame:</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Willie, Hound Dog, Honey Boy, Sal,</div>\r\n<div class=''poempara''>the many legends who&rsquo;ve shared the marrow</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; of their music, of Santana&rsquo;s hearty</div>\r\n<div class=''poempara''><em>soup with the bones still in it</em> steaming with sabor</div>\r\n<div class=''poempara''>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; and sometimes weeping.</div></div>\r\n				</div>\r\n			\r\n		\r\n		\r\n			<div class="credit">\r\n					\r\n					\r\n					<p>\r\n					Brenda C&aacute;rdenas, &ldquo;At the Base of the Blues Tree: Una Ofrenda for Jimmy Davis, Chicago Blues Man&rdquo; from<em> Boomerang.</em> Copyright &copy; 2009 by Bilingual Press/Editorial Biling&uuml;e. Reprinted with permission.﻿<br /><br />\r\n				</p>\r\n			</div>', 4),
('5', '5', '"Chicago Poem"', '<p>&nbsp;&nbsp;&nbsp;&nbspI lived here nearly 5 years before I could\r\n     meet the middle western day with anything approaching\r\nDignity. It’s a place that lets you\r\n     understand why the Bible is the way it is:\r\nProud people cannot live here.</p>', '<p>&nbsp;&nbsp;&nbsp;&nbspI lived here nearly 5 years before I could\r\n     meet the middle western day with anything approaching\r\nDignity. It’s a place that lets you\r\n     understand why the Bible is the way it is:\r\nProud people cannot live here...</p>', 5),
('6', '6', 'Homan and Chicago Ave.', '<div style=''text-indent: -1em; padding-left: 1em;''>Cross the blood </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>that quilts your busted lip </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>with the tender tip... </div>', '<div class="poem">\r\n					\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>Cross the blood </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>that quilts your busted lip </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>with the tender tip </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>of   your tongue. That lip&rsquo;s </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>blood is brackish and white </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>meat flares from the black </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>swell. You crossed your mama&rsquo;s </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>mind so call her sometimes. </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>She dreams your dead daddy </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>still puts his hands on her </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>waist. She calls his name </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>then crosses herself, calls </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>the police then crosses </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>her fingers. Cross me </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and get cut across your cheek, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>its fat bag full of   bad words </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and cheap liquor you hide </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>from your badass kids. Make </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>a wish for bad weather </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>when the hoodlums get to shooting </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in a good summer&rsquo;s heat. </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Cross the territory between </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>two gangs and feel eyes stare </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and cross in a blur of crosshairs. </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>When a shot man lands </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in the garden of trash the block </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>flares up like an appetite </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>spurred on by the sight </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>of prey, by the slurred </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>prayer of a man so death-close </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>he sees buzzards burrow </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>their bladed beaks into </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>his entry wound. Tune </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>the trumpets. Make way through </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>dusk&rsquo;s clutter. After death </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>the dead cross over into song, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>their bones tuning-forked </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>into vibrancy. Cross your lips, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>mutiny against all speech </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>when a corpse starts singing </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>despite its leaded larynx. Don&rsquo;t </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>say miracle when butterflies </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>break from a death-gaped skull, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>rout the sky, and scatter.</div>\r\n				</div>\r\n		\r\n		\r\n		\r\n			<div class="credit">\r\n					\r\n					\r\n					<p>\r\n					\r\n					Source: <em>Poetry</em> (November 2013).\r\n					\r\n				</p>\r\n			</div>', 6),
('7', '7', '"kitchenette building"', '<div style=''text-indent: -1em; padding-left: 1em;''>We are things of dry hours and the involuntary plan,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Grayed in, and gray. &ldquo;Dream&rdquo; makes a giddy sound, not strong...</div>', '<div style=''text-indent: -1em; padding-left: 1em;''>We are things of dry hours and the involuntary plan,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Grayed in, and gray. &ldquo;Dream&rdquo; makes a giddy sound, not strong</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Like &ldquo;rent,&rdquo; &ldquo;feeding a wife,&rdquo; &ldquo;satisfying a man.&rdquo;</div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>But could a dream send up through onion fumes</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Its white and violet, fight with fried potatoes</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>And yesterday&rsquo;s garbage ripening in the hall,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Flutter, or sing an <span id="annotation-2" class="annotation">aria</span><span id="annotation-2-text" style="display: none;"><strong>aria </strong>an elaborate song for one voice with orchestral accompaniment, appearing most often in opera (&ldquo;aria&rdquo; means &ldquo;air&rdquo; in Italian).</span> down these rooms</div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Even if we were willing to let it in,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Had time to warm it, keep it very clean,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Anticipate a message, let it begin?</div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>We wonder. But not well! not for a minute!</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>Since Number Five is out of the bathroom now,</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>We think of lukewarm water, hope to get in it.</div>	\r\n\r\n	<div class="credit">\r\n					\r\n		<p>\r\n				Source: <em>Selected Poems</em> (Harper & Row, 1963).\r\n		</p>\r\n	</div>	', 7),
('8', '8', '"Little Wife"', '<p class="epigraph"><i>At the Oriental Institute, Chicago</i></p>\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>They redid King Tut splendid, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>once stone-huge as this ...</div>', '<div class="poem">\r\n					<p class="epigraph"><i>At the Oriental Institute, Chicago</i></p>\r\n					<div style=''text-indent: -1em; padding-left: 1em;''>They redid King Tut splendid, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>once stone-huge as this </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>yet his wife&rsquo;s feet </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>tiny, the only thing of her now</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>low, next to him. A few toes, some of the rest, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>a bit of ankle, that&rsquo;s it</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>in the shade of her husband&rsquo;s looming, massive </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>looking straight ahead into the future </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>where we live and can&rsquo;t</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>eye-to-eye, where to stare at him</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>is to suffer <em>warbler neck</em>, head back and up</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&agrave; la the high just-leafing-out trees as bright bits</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>wing their blink</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and hide. Little wife, </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>such small feet, the thought </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>dwarfs the king </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>as ache, as what is </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>ever left of us </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>and oh, I like her better.</div>\r\n				</div>\r\n			\r\n		\r\n		\r\n			<div class="credit">\r\n					\r\n					\r\n					<p>\r\n					\r\n					Source: <em>Poetry</em> (November 2011).\r\n					\r\n				</p>\r\n			</div>', 8),
('9', '7', '"We Real Cool"', '<p class="epigraph"><div></div>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>The Pool Players. <br />&nbsp; &nbsp; &nbsp; &nbsp; Seven at the Golden Shovel.</em></p>\r\n					<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; We real cool. We&nbsp;&nbsp;&nbsp;...</div>', ' <div class="poem">\r\n					<p class="epigraph"><div></div>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>The Pool Players. <br />&nbsp; &nbsp; &nbsp; &nbsp; Seven at the Golden Shovel.</em></p>\r\n					<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; We real cool. We&nbsp;&nbsp;&nbsp;</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Left school. We </div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Lurk late. We </div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Strike straight. We </div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Sing sin. We&nbsp;&nbsp;&nbsp;</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Thin gin. We </div>\r\n<br/>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Jazz June. We&nbsp;&nbsp;&nbsp;</div>\r\n<div style=''text-indent: -1em; padding-left: 1em;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Die soon.</div>\r\n				</div>\r\n			\r\n		\r\n		\r\n			<div class="credit">\r\n					\r\n					\r\n					<p>\r\n					Gwendolyn Brooks, &ldquo;We Real Cool&rdquo; from <em>Selected Poems.</em> Copyright &copy; 1963 by Gwendolyn Brooks. Reprinted with the permission of the Estate of Gwendolyn Brooks.<br /><br />\r\n					\r\n				</p>\r\n			</div>', 9);

